const form=document.querySelector("#task-form"),tasklist=document.querySelector(".tasklist"),clearTasks=document.querySelector(".clear-tasks"),filter=document.querySelector("#filter"),taskInput=document.querySelector("#task");function loadEventListeners(){form.addEventListener("submit",addTask),tasklist.addEventListener("click",removeTask),clearTasks.addEventListener("click",clearAllTasks),filter.addEventListener("keyup",filterTasks)}function addTask(e){if(""===taskInput.value)alert("No task name given."),e.preventDefault();else{const t=document.createElement("li");t.className="task",t.appendChild(document.createTextNode(taskInput.value));const a=document.createElement("a");a.className="delete-task",a.innerHTML='<i class="far fa-trash-alt"></i>',t.appendChild(a),tasklist.appendChild(t),taskInput.value="",e.preventDefault()}}function removeTask(e){e.target.parentElement.classList.contains("delete-task")&&(confirm("Delete task:"+e.target.parentElement.parentElement.textContent)&&e.target.parentElement.parentElement.remove(),console.log(e.target))}function clearAllTasks(){if(confirm("This will delete ALL tasks"))for(;tasklist.firstChild;)tasklist.removeChild(tasklist.firstChild)}function filterTasks(e){const t=e.target.value.toLowerCase();document.querySelectorAll(".task").forEach(function(e){-1!==e.firstChild.textContent.toLowerCase().indexOf(t)?e.style.display="block":e.style.display="none"})}loadEventListeners();